CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;


DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20230731035948_createtblItems') THEN
    CREATE TABLE "Items" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "ItemCode" text NOT NULL,
        "Description1" text NOT NULL,
        "Description2" text NULL,
        "Inventory" text NOT NULL,
        "GLCOSG" text NULL,
        "ItemType" text NOT NULL,
        "ItemClass" text NOT NULL,
        "ItemGroup" text NOT NULL,
        "BaseUnit" text NOT NULL,
        "CostUnit" text NOT NULL,
        "CreatedBy" text NOT NULL,
        "CreatedDate" text NOT NULL,
        "ModifiedBy" text NOT NULL,
        "ModifiedDate" text NOT NULL,
        "Cancelled" boolean NOT NULL,
        CONSTRAINT "PK_Items" PRIMARY KEY ("Id")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20230731035948_createtblItems') THEN
    INSERT INTO "__EFMigrationsHistory" ("MigrationId", "ProductVersion")
    VALUES ('20230731035948_createtblItems', '7.0.9');
    END IF;
END $EF$;
COMMIT;

